import {useState} from 'react';

import kitty from './kitty-icon.svg'

import './SideInfo.css';

function SideInfo() {
  const [isOpen, setIsOpen] = useState(false);

  const handleOpen = () => {
    setIsOpen(current => !current);
  };

  const team = [
    "Katerina Loschinina",
    "Andrey Novikov",
    "Lucy Onischenko",
    "Alex Ostrovsky",
    "Paulo Ricca",
  ]

  return (
    <div className={isOpen ? "side-info open" : "side-info"}>
      <img 
        src={kitty}
        className="info-icon" 
        alt="kitty-icon"
        onClick={handleOpen}></img>
      <h3>Who are Ko-Tyat-Ki Kollektiv?</h3>
      <p>We are a London-based techno-art collective exploring perception, inception &#38; conception. We've successfully built art installations at regional BM events</p>
      <h3>Team</h3>
      <ul>
        {team.sort((a,b) => a - b).map((name) => (<li>{name}</li>))}
      </ul>
    </div>
  );
}

export default SideInfo;
