  const handleClick = () => {
    // üëáÔ∏è toggle
    setIsActive(current => !current);

    // üëáÔ∏è or set to true
    // setIsActive(true);
  };

  return (
    <div className="side-info open">
      <img 
        src={kitty}
        className="info-icon" 
        alt="kitty-icon"
        onClick={handleOpen}></img>
    </div>
  );
}

export default SideInfo;
