import {useState} from 'react';

import kitty from './kitty-icon.svg'

import './SideInfo.css';

function SideInfo() {
  const [isOpen, setIsOpen] = useState(false);

  const handleOpen = () => {
    setIsOpen(current => !current);
  };

  return (
    <div className={isOpen ? "side-info open" : "side-info"}>
      <img 
        src={kitty}
        className="info-icon" 
        alt="kitty-icon"
        onClick={handleOpen}></img>
      <h3>Who are Ko-Tyat-Ki Kollektiv?</h3>
      <p>We are a London-based techno-art collective exploring perception, inception & conception. We've successfully built art installations at regional BM events</p>
    </div>
  );
}

export default SideInfo;
